import React from 'react';
import {useParams} from 'react-router-dom';

import Input from '../../shared/components/FormElement/Input';
import Button from '../../shared/components/FormElement/Button';
import { VALIDATOR_REQUIRE,VALIDATOR_MINLENGTH } from '../../shared/components/util/validators';
import './PlaceForrm.css';

const DUMMY_PLACES =[
    {
        id:'p1',
        title:'Empire State Building',
        description:'One of the most famous sky scrapers in the world',
        imageUrl:'https://lh5.googleusercontent.com/p/AF1QipNAZWH5XAJT81otOykRk8j8wQR6u_Nle08TTGym=w408-h272-k-no',
        address:'20 W 34th St, New York, NY 10001, United States',
        location:{
            lat:40.7484405,
            lng:-73.9856644
        },
        creator:'u1'
    },
    
    {
        id:'p2',
        title:'Empire State Building',
        description:'One of the most famous sky scrapers in the world',
        imageUrl:'https://lh5.googleusercontent.com/p/AF1QipNAZWH5XAJT81otOykRk8j8wQR6u_Nle08TTGym=w408-h272-k-no',
        address:'20 W 34th St, New York, NY 10001, United States',
        location:{
            lat:40.7484405,
            lng:-73.9856644
        },
        creator:'u2'
    }

];

const UpdatePlace = () =>{
    const placeId = useParams().placeId;

    const identifiedPlace = DUMMY_PLACES.find(p=> p.id === placeId);

    if(!identifiedPlace){
        return(
            <div className="center">
                <h2>Could not find place!</h2>
            </div>
        );
    }

    return (
      <form className="place-form">
        <Input
          id="title"
          element="input"
          type="text"
          label="Title"
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please enter the valid title."
          onInput={() => {}} 
          value={identifiedPlace.title}
          valid={true}
        />
        <Input
          id="description"
          element="textarea"
          label="Title"
          validators={[VALIDATOR_MINLENGTH(5)]}
          errorText="Please enter the at least 5 character "
          onInput={() => {}} 
          value={identifiedPlace.description}
          valid={true}
        />
        <Button type="submit" disabled={true}>
            UPDATE PLACE
        </Button>
      </form>
    );
};

export default UpdatePlace;
